svg.script:
//

var params=new URL(window.location.href).searchParams;
var h=1,b=params.get("content-wbyh"),w=(params.get("border-wbyh"))/2;
var svg_outer_height=parseFloat(params.get("content-height"))+parseFloat(400*w);

var svg=document.getElementsByTagName('svg')[0];


var polygon=document.getElementsByTagName('polygon')[0];

polygon.setAttribute("points",'0,0 '+(parseFloat(b)+parseFloat(w))+',0 '+(parseFloat(b)+parseFloat(2*w)+parseFloat(h/3))+','+(parseFloat(h/3)+parseFloat(w)*(1-Math.tan(22.5)))+' '+(parseFloat(b)+parseFloat(2*w)+parseFloat(h/3))+','+(parseFloat(w)*(1+Math.tan(22.5))+parseFloat((2*h)/3))+' '+(parseFloat(b)+parseFloat(w))+','+(parseFloat(2*w)+parseFloat(h))+' 0,'+(parseFloat(2*w)+parseFloat(h)));

polygon.setAttribute("stroke",params.get("border-color").replace("hash","#"));
polygon.setAttribute("stroke-width",params.get("border-wbyh"));

svg.setAttribute("viewBox",(-1*w)+" "+(-1*w)+" "+(parseFloat(b)+parseFloat(h/3)+parseFloat(w*4))+" "+(parseFloat(h)+parseFloat(w*4)));

svg.setAttribute("fill",params.get("fill-color").replace("hash","#"));

svg.appendChild(polygon);

svg.style.height=svg_outer_height+"px";
//
